import React, { useState, useRef, useEffect } from 'react';

const WebcamRecorder = () => {
    const [isRecording, setIsRecording] = useState(false);
    const [stream, setStream] = useState(null);
    const videoRef = useRef(null);
    const mediaRecorderRef = useRef(null);
    const recordedChunks = useRef([]);

    useEffect(() => {
        const handleResize = () => {
            if (videoRef.current) {
                const videoHeight = window.innerHeight * 0.8; // Adjust the factor (0.8) as needed
                videoRef.current.style.maxHeight = `${videoHeight}px`;
            }
        };

        handleResize(); // Set initial height
        window.addEventListener('resize', handleResize);

        return () => {
            window.removeEventListener('resize', handleResize);
        };
    }, []);

    const startRecording = async () => {
        try {
            const constraints = { video: { width: 720, height: 1280 } };
            const newStream = await navigator.mediaDevices.getUserMedia(constraints);
            setStream(newStream);
            const mediaRecorder = new MediaRecorder(newStream);

            mediaRecorder.start();
            setIsRecording(true);

            mediaRecorderRef.current = mediaRecorder;
            mediaRecorder.ondataavailable = (event) => {
                recordedChunks.current.push(event.data);
            };

            mediaRecorder.onstop = () => {
                const blob = new Blob(recordedChunks.current, { type: 'video/mp4; codecs="avc1.42E01E, mp4a.40.2"' });
                const url = URL.createObjectURL(blob);
                console.log('Recorded video URL:', url);
                recordedChunks.current = [];
            };

            videoRef.current.srcObject = newStream;
        } catch (error) {
            console.error('Error accessing webcam:', error);
        }
    };

    const stopRecording = () => {
        if (mediaRecorderRef.current && mediaRecorderRef.current.state !== 'inactive') {
            mediaRecorderRef.current.stop();
        }
        if (stream) {
            stream.getTracks().forEach((track) => track.stop());
            setStream(null);
            videoRef.current.srcObject = null;
        }
        setIsRecording(false);
    };

    return (
        <div onLoad={startRecording}>
            <video ref={videoRef} autoPlay></video>
            <div>
                {isRecording ? <button onClick={stopRecording}>Stop Recording</button> : null}
            </div>
        </div>
    );
};

export default WebcamRecorder;
