import React, { useState, useRef, useEffect } from 'react';
import * as cv from 'opencv.js';

function WebcamRecorder() {
    const [isRecording, setIsRecording] = useState(false);
    const [stream, setStream] = useState(null);
    const videoRef = useRef(null);
    const canvasRef = useRef(null);
    const mediaRecorderRef = useRef(null);
    const recordedChunks = useRef([]);
    const captureInterval = useRef(null);

    useEffect(() => {
        startRecording();
        function handleResize() {
            if (videoRef.current && canvasRef.current) {
                const videoHeight = window.innerHeight * 0.9;
                videoRef.current.style.maxHeight = `${videoHeight}px`;
                canvasRef.current.style.maxHeight = `${videoHeight}px`;
            }
        }

        handleResize();
        window.addEventListener('resize', handleResize);

        return () => {
            window.removeEventListener('resize', handleResize);
        };
    }, []);

    const startRecording = async () => {
        try {
            const constraints = { video: { width: 720, height: 1280 } };
            const newStream = await navigator.mediaDevices.getUserMedia(constraints);
            setStream(newStream);
            const mediaRecorder = new MediaRecorder(newStream);

            mediaRecorder.start();
            setIsRecording(true);

            mediaRecorderRef.current = mediaRecorder;
            mediaRecorder.ondataavailable = (event) => {
                recordedChunks.current.push(event.data);
            };

            mediaRecorder.onstop = () => {
                const blob = new Blob(recordedChunks.current, { type: 'video/mp4; codecs="avc1.42E01E, mp4a.40.2"' });
                const url = URL.createObjectURL(blob);
                console.log('Recorded video URL:', url);
                recordedChunks.current = [];
            };

            videoRef.current.srcObject = newStream;
            captureInterval.current = setInterval(captureImage, 5000); // capture img every 5 secs
        } catch (error) {
            console.error('Error accessing webcam:', error);
        }
    }

    function stopRecording() {
        if (mediaRecorderRef.current && mediaRecorderRef.current.state !== 'inactive') {
            mediaRecorderRef.current.stop();
        }
        if (stream) {
            stream.getTracks().forEach((track) => track.stop());
            setStream(null);
            videoRef.current.srcObject = null;
        }
        if (captureInterval.current) {
            clearInterval(captureInterval.current);
        }
        setIsRecording(false);
        window.location.replace("http://localhost:3000") //TODO REPLACE URL AFTER TESTING!
    }

    function captureImage() {
        const canvas = canvasRef.current;
        const video = videoRef.current;
        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;
        const context = canvas.getContext('2d');
        context.drawImage(video, 0, 0, canvas.width, canvas.height);

        // Convert the image to grayscale
        const imageData = context.getImageData(0, 0, canvas.width, canvas.height);
        const src = cv.matFromImageData(imageData);
        const gray = new cv.Mat();
        cv.cvtColor(src, gray, cv.COLOR_RGBA2GRAY);

        // Apply Canny edge detection
        const edges = new cv.Mat();
        cv.Canny(gray, edges, 50, 150);

        // Find contours
        const contours = new cv.MatVector();
        const hierarchy = new cv.Mat();
        cv.findContours(edges, contours, hierarchy, cv.RETR_EXTERNAL, cv.CHAIN_APPROX_SIMPLE);

        // Check if any contours are detected (indicating roads)
        const hasRoads = contours.size() > 0;

        if (!hasRoads) {
            // Draw blue overlay with message
            context.fillStyle = 'rgba(0, 0, 255, 0.5)';
            context.fillRect(0, 0, canvas.width, canvas.height);
            context.fillStyle = '#ffffff';
            context.font = '30px Arial';
            context.textAlign = 'center';
            context.fillText('Point toward roads please', canvas.width / 2, canvas.height / 2);
        }

        // Clean up
        src.delete();
        gray.delete();
        edges.delete();
        contours.delete();
        hierarchy.delete();
    }

    return (
        <div>
            <video className="recording" ref={videoRef} autoPlay></video>
            <canvas ref={canvasRef} style={{ display: 'none' }}></canvas>
            <div>
                {isRecording ? (
                    <button className="button stop" onClick={stopRecording}>Stop Recording</button>
                ) : null}
            </div>
        </div>
    );
}

export default WebcamRecorder;
