import './App.css';
import WebcamRecorder from "./components/recorder";
import {useEffect, useRef} from "react";

function App() {
    const videoRef = useRef(null);

    useEffect(() => {
        function handleResize() {
            if (videoRef.current) {
                const videoHeight = window.innerHeight * 0.9; // Adjust the factor (0.8) as needed
                videoRef.current.style.maxHeight = `${videoHeight}px`;
            }
        }

        handleResize();
        window.addEventListener('resize', handleResize);

        return () => {
            window.removeEventListener('resize', handleResize);
        };
    }, []);

  return (
    <div className="container alignMiddle">
      <div className="videoContainer alignMiddle">
          <WebcamRecorder videoRef={videoRef}/>
      </div>
    </div>
  );
}

export default App;
